//SPDX-License-Identifier: MIT
pragma solidity >= 0.5.0 < 0.7.0;

import "./ValidationContract.sol";

contract RegistryContract {

    event ValidationContractAddressUpdated(address newValidationContractAddress);

    address owner;
    address public validationContractAddress;

  struct Device {
    bytes32 did;
  }

  constructor(address _validationContractAddress) public {
    owner = msg.sender;
    validationContractAddress = _validationContractAddress;
  }

      modifier onlyOwner() {
        require(msg.sender == owner, "Sender does not equal contract owner.");
        _;
    }

        modifier onlyValidationContract() {
        require (validationContractAddress == msg.sender, "Sender address does not equal voting contract address.");
        _;
    }

      mapping(uint => Device) public devices;
      mapping(bytes32 => Device) public dids;

      function registerDevice(bytes32 _deviceDID, uint _physicalId, bytes32 _aktDID) public {
        Device storage device = devices[_physicalId];
        Device storage did = dids[_deviceDID];

        require(dids[_deviceDID].did == 0, "This DID already exists!");
        require(devices[_physicalId].did == 0, "Theres already a DID registered for this pyhsicalId!");

        checkValidity(_physicalId, _aktDID);

        device.did = _deviceDID;

        did.did = _deviceDID;
      }

      function getDevice(uint _physicalId) view public returns(bytes32){
       return devices[_physicalId].did;
      }

      function checkValidity(uint _phyisicalId, bytes32 _aktDID) internal view returns(bool) {

          ValidationContract v = ValidationContract(validationContractAddress);

          bool enabledDeviceAktDid = v.isAllowed(_phyisicalId, _aktDID);

          require(enabledDeviceAktDid == true, "This physicalId is not enabled for registering");
      }

}
