//SPDX-License-Identifier: MIT
pragma solidity >= 0.5.0 < 0.7.0;



contract ValidationContract {

    address owner;
    bytes32 aktDID;

  struct Device {
    uint physicalId;
    }

  constructor() public {
    owner = msg.sender;
  }

      modifier onlyOwner() {
        require(msg.sender == owner, "Sender does not equal contract owner.");
        _;
    }

        function setAktDID(bytes32 _aktDID) public onlyOwner {

        if (aktDID == bytes32(0)) {
            aktDID = _aktDID;
        } else {
            revert("Validation contract address can only be set initially.");
        }
        }

      mapping(uint => Device) public devices;

      function setPhysicalId(uint _physicalId) public onlyOwner {
        Device storage device = devices[_physicalId];

        require(devices[_physicalId].physicalId != _physicalId, "This phyisicalId has already been entered for registering");

        device.physicalId = _physicalId;

      }

      function isAllowed(uint _physicalId, bytes32 _aktDID) public view returns(bool) {
        require(devices[_physicalId].physicalId == _physicalId, "This phyisicalId has not been entered for registering");
        require(aktDID == _aktDID, "This Activation Device is not allowed to register IDs");

        return true;

      }


}
